import React, { createContext, useContext, useState, useEffect } from 'react';
import { format } from 'date-fns';
import { generateQRCodeForCertificate } from '../utils/qrCodeGenerator';
import { generateCertificateHash } from '../utils/blockchain';


interface Certificate {
  id: string;
  studentName: string;
  studentEmail: string;
  institutionName: string;
  institutionId: string;
  courseName: string;
  grade: string;
  issueDate: string;
  completionDate: string;
  blockchainHash: string;
  ipfsHash: string;
  qrCode: string;
  isVerified: boolean;
  verificationStatus: 'pending' | 'verified' | 'rejected';
  // Optional verifier request metadata when a student requests verification
  verifierRequest?: {
    name: string;
    email: string;
    requestedAt: string;
  } | null;
  fileUrl?: string;
  certificateType: 'degree' | 'diploma' | 'certificate' | 'transcript';
}

interface CertificateContextType {
  certificates: Certificate[];
  addCertificate: (certificate: Omit<Certificate, 'id' | 'blockchainHash' | 'ipfsHash' | 'qrCode' | 'isVerified'>) => Promise<string>;
  verifyCertificate: (certificateId: string) => Certificate | null;
  getCertificatesByStudent: (studentEmail: string) => Certificate[];
  getCertificatesByInstitution: (institutionId: string) => Certificate[];
  updateCertificateStatus: (certificateId: string, status: 'verified' | 'rejected') => void;
  requestVerification: (certificateId: string, verifierName: string, verifierEmail: string) => Promise<boolean>;
}

const CertificateContext = createContext<CertificateContextType | undefined>(undefined);

export const useCertificates = () => {
  const context = useContext(CertificateContext);
  if (!context) {
    throw new Error('useCertificates must be used within a CertificateProvider');
  }
  return context;
};

export const CertificateProvider: React.FC<{ children: React.ReactNode }> = ({ children }) => {
  const [certificates, setCertificates] = useState<Certificate[]>([]);

  useEffect(() => {
    // Load certificates from localStorage
    const storedCertificates = localStorage.getItem('certificates');
    if (storedCertificates) {
      setCertificates(JSON.parse(storedCertificates));
    } else {
      // Initialize with demo certificates
      const demoCertificates: Certificate[] = [
        {
          id: 'cert-001',
          studentName: 'Alice Johnson',
          studentEmail: 'student@demo.com',
          institutionName: 'Stanford University',
          institutionId: '2',
          courseName: 'Computer Science - Bachelor of Science',
          grade: 'A',
          issueDate: format(new Date(), 'yyyy-MM-dd'),
          completionDate: format(new Date(Date.now() - 30 * 24 * 60 * 60 * 1000), 'yyyy-MM-dd'),
          blockchainHash: '0x1234567890abcdef...',
          ipfsHash: 'QmYjtig7VJQ6XsnUjqqJvj7QaMcCAwtrgNdahSiFofrE7o',
          qrCode: 'data:image/png;base64,',
          isVerified: true,
          verificationStatus: 'verified',
          certificateType: 'degree'
        }
      ];
      setCertificates(demoCertificates);
      localStorage.setItem('certificates', JSON.stringify(demoCertificates));
    }
  }, []);


  const addCertificate = async (certificateData: Omit<Certificate, 'id' | 'blockchainHash' | 'ipfsHash' | 'qrCode' | 'isVerified'>): Promise<string> => {
    const id = `cert-${Date.now()}`;
    const dataToHash = JSON.stringify(certificateData);
    const blockchainHash = await generateCertificateHash(certificateData);
    const ipfsHash = `Qm${Math.random().toString(36).substr(2, 42)}`;
    
    // Generate QR code as data URL
    const qrCode = await generateQRCodeForCertificate(id);

    const newCertificate: Certificate = {
      ...certificateData,
      id,
      blockchainHash,
      ipfsHash,
      qrCode,
      isVerified: false, // Start as unverified
      verificationStatus: 'pending'
    };

    console.log("certification data",certificateData);

    const updatedCertificates = [...certificates, newCertificate];
    setCertificates(updatedCertificates);
    localStorage.setItem('certificates', JSON.stringify(updatedCertificates));
    // Try to persist certificate to backend (which inserts into Supabase)
    try {
  const API_URL = import.meta.env.VITE_API_URL || 'http://localhost:4000';
  // AuthContext stores the token as 'ec_token' — include that first, then fall back to older keys
  const token = localStorage.getItem('ec_token') || localStorage.getItem('token') || localStorage.getItem('authToken') || undefined;

      const payload = {
        student_name: certificateData.studentName,
        student_email: certificateData.studentEmail,
        course_name: certificateData.courseName,
        certificate_type: certificateData.certificateType,
        grade: certificateData.grade,
        completion_date: certificateData.completionDate || null,
        issuer_institution_id: certificateData.institutionId || null
      };

      const resp = await fetch(`${API_URL}/api/certificates`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          ...(token ? { Authorization: `Bearer ${token}` } : {})
        },
        body: JSON.stringify(payload)
      });

      const persistOk = resp.ok;
      if (persistOk) {
        const json = await resp.json();
        // if backend returned created certificate, replace local id with server id
        if (json && json.certificate && json.certificate.id) {
          const serverId = json.certificate.id as string;
          const reconciled = updatedCertificates.map(c => c.id === id ? { ...c, id: serverId } : c);
          setCertificates(reconciled);
          localStorage.setItem('certificates', JSON.stringify(reconciled));
        }
      } else {
        console.warn('Failed to persist certificate to backend', await resp.text());
      }

      // Send certificate email to student
      try {
        await fetch(`http://localhost:5000/send-email/certificate`, {
          method: 'POST',
          headers: { 'Content-Type': 'application/json' },
          body: JSON.stringify({
            to: newCertificate.studentEmail,
            studentName: newCertificate.studentName,
            courseName: newCertificate.courseName,
            grade: newCertificate.grade,
            institutionName: newCertificate.institutionName,
            issueDate: newCertificate.issueDate,
            completionDate: newCertificate.completionDate,
            certificateId: newCertificate.id
          })
        });
      } catch (error) {
        console.error('Failed to send certificate email:', error);
      }
    } catch (err) {
      console.error('Error persisting certificate to backend:', err);
    }
    
    return id;
  };

  const verifyCertificate = (certificateId: string): Certificate | null => {
    return certificates.find(cert => cert.id === certificateId) || null;
  };

  const getCertificatesByStudent = (studentEmail: string): Certificate[] => {
    return certificates.filter(cert => cert.studentEmail === studentEmail);
  };

  const getCertificatesByInstitution = (institutionId: string): Certificate[] => {
    return certificates.filter(cert => cert.institutionId === institutionId);
  };

  const updateCertificateStatus = async (certificateId: string, status: 'verified' | 'rejected'): Promise<void> => {
    const updatedCertificates = certificates.map(cert => 
      cert.id === certificateId 
        ? { ...cert, verificationStatus: status, isVerified: status === 'verified' }
        : cert
    );
    setCertificates(updatedCertificates);
    localStorage.setItem('certificates', JSON.stringify(updatedCertificates));

    try {
      const API_URL = import.meta.env.VITE_API_URL || 'http://localhost:5000';
      const token = localStorage.getItem('ec_token') || localStorage.getItem('token') || localStorage.getItem('authToken') || undefined;

      await fetch(`${API_URL}/api/certificates/${certificateId}/status`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          ...(token ? { Authorization: `Bearer ${token}` } : {})
        },
        body: JSON.stringify({ status })
      });
    } catch (err) {
      console.error('Error updating certificate status in backend:', err);
    }
  };

  // Student requests a verifier to verify a specific certificate
  const requestVerification = async (certificateId: string, verifierName: string, verifierEmail: string): Promise<boolean> => {
    try {
      const API_URL = import.meta.env.VITE_API_URL || 'http://localhost:4000';
      const token = localStorage.getItem('ec_token') || localStorage.getItem('token') || localStorage.getItem('authToken') || undefined;

      // Get verifier id from email
      const userResp = await fetch(`${API_URL}/api/users/by-email/${verifierEmail}`, {
        headers: {
          ...(token ? { Authorization: `Bearer ${token}` } : {})
        }
      });

      if (!userResp.ok) {
        console.warn('Failed to get verifier id from email', await userResp.text());
        return false;
      }

      const { user } = await userResp.json();
      const verifierId = user.id;

      const updatedCertificates = certificates.map((cert): Certificate => {
        if (cert.id === certificateId) {
          return {
            ...cert,
            verificationStatus: 'pending',
            isVerified: false,
            verifierRequest: {
              name: verifierName,
              email: verifierEmail,
              requestedAt: new Date().toISOString()
            }
          } as Certificate;
        }
        return cert;
      }) as Certificate[];

      setCertificates(updatedCertificates);
      localStorage.setItem('certificates', JSON.stringify(updatedCertificates));

      // Find the certificate to send in the email
        const certificate = updatedCertificates.find(c => c.id === certificateId);

        // Persist verification request to backend (will create verification_requests row)
        try {
          // backend accepts verifier_id; if we only have email/name we pass null and include details in message
          const resp = await fetch(`${API_URL}/api/certificates/${certificateId}/request-verification`, {
            method: 'POST',
            headers: {
              'Content-Type': 'application/json',
              ...(token ? { Authorization: `Bearer ${token}` } : {})
            },
            body: JSON.stringify({ verifier_id: verifierId, message: JSON.stringify({ name: verifierName, email: verifierEmail }) })
          });

          if (resp.ok) {
            console.log('Verification request persisted to backend');
          } else {
            console.warn('Failed to persist verification request to backend', await resp.text());
          }
        } catch (err) {
          console.error('Error persisting verification request to backend:', err);
        }

        if (certificate) {
          try {
            await fetch(`http://localhost:5000/send-email/verifier`, {
              method: 'POST',
              headers: { 'Content-Type': 'application/json' },
              body: JSON.stringify({
                to: verifierEmail,
                verifierName: verifierName,
                studentName: certificate.studentName,
                courseName: certificate.courseName,
                institutionName: certificate.institutionName,
                issueDate: certificate.issueDate,
                certificateId: certificate.id
              })
            });
          } catch (error) {
            console.error('Failed to send verifier email:', error);
          }
        }

        return true;
    } catch (error) {
      console.error('Error requesting verification:', error);
      return false;
    }
  };

  return (
    <CertificateContext.Provider value={{
      certificates,
      addCertificate,
      verifyCertificate,
      getCertificatesByStudent,
      getCertificatesByInstitution,
  updateCertificateStatus,
  requestVerification
    }}>
      {children}
    </CertificateContext.Provider>
  );
};